package app

import (
	"crypto/rsa"
	"github.com/insan1a/tech-tinker/internal/lib/decoder"
)

type config struct {
	RSA
}

type RSA struct {
	publicKey  *rsa.PublicKey
	privateKey *rsa.PrivateKey
}

func newConfig() (*config, error) {
	privateKey, err := decoder.DecodeRSAPrivateKey("")
	if err != nil {
		return nil, err
	}

	publicKey, err := decoder.DecodeRSAPublicKey("")
	if err != nil {
		return nil, err
	}

	cfg := &config{
		RSA{
			publicKey:  publicKey,
			privateKey: privateKey,
		},
	}
	return cfg, nil
}
